policies.inbound:
		
		
		
			https://otdsext-dev.niaid.nih.gov/otdsws/oauth2/token
			POST
			
				application/x-www-form-urlencoded
			
			@("grant_type=client_credentials&client_id=azure-poc&client_secret=xxxxxxxxxxxxxxxxxxx&scope=resource:d8e7d981-7ae7-4228-ba8d-956cfb04bf22")
		
		
			@{
                var responseVar = (IResponse)context.Variables["tokenResponse"];
                return responseVar.Body.As(preserveContent: true);
            }
		
		
		<set-variable name="accessToken" value="@(((IResponse)context.Variables["tokenResponse"]).Body.As()["access_token"].ToString())" />
		
		
			@("Bearer " + context.Variables["accessToken"])
